<html><head>	<title>jsTouch</title>	<meta name="viewport" content="initial-scale=1.0; maximum-scale=1.0; user-scalable=no; width=device-width;" />	<meta name="apple-mobile-web-app-capable" content="yes" />	<meta name="apple-mobile-web-app-status-bar-style" content="black" />	<link rel="apple-touch-startup-image" href="images/iphone_startup.png" />	<link rel="apple-touch-icon" href="images/iphone_icon.png" />	<link id="mainCSS" type="text/css" rel="stylesheet" media="screen" href="css/ipad-dark.css">	<script type="text/javascript" src="includes/jquery.js"></script>	<script type="text/javascript" src="includes/jsTouch.js"></script>	<script type="text/javascript" src="includes/iScroll.js"></script>	<script type="text/javascript">		var myTouch;		$(document).ready(function () { 			myTouch = new jsTouch('myTouch', { width: 320 } );			myTouch.loadPage('pages/home.php');						myTouch2 = new jsTouch('myTouch2', { width: window.innerWidth - 320 } );			myTouch2.loadPage('pages/home.php'); 		});		// prevent default scroll 		document.addEventListener('touchmove', function (e) { e.preventDefault(); }, false);	</script></head><body>	<div id="myTouch" style="position: absolute; left: 0px; top: 0px; background-color: black; overflow: hidden;">	</div> 	<div id="myTouch2" style="position: absolute; left: 320px; top: 0px; border-left: 2px solid #666666; background-color: black; overflow: hidden;">	</div> </body></html>